import React,{useEffect, useState, useContext} from 'react'
import { UserContext } from '../../App'

function Myprofile() {
    const [allpics, setPics]=useState([])
    const {state, dispatch} = useContext(UserContext)
    const [image, setImage] = useState('')
    const [url, setUrl]=useState("")

    useEffect(() => {
        fetch('http://localhost:1000/mypost',{
            headers:{
                "Authorization":"Hello " +localStorage.getItem("jwt")
            }
        }).then(res=>res.json())
        .then(result=>{
            console.log(result)
            setPics(result.mypost)
        })
    }, [])
    
    useEffect(()=>{ 
        if (image){
            const data = new FormData()
            data.append("file", image)
            data.append("upload_preset", "insta-clone")
            data.append("cloud_name", "siddtechbadal")
            fetch("https://api.cloudinary.com/v1_1/siddtechbadal/image/upload",{
                method:"post",
                body:data
            }).then(res=>res.json())
            .then(data=>{
                setUrl(data.url)
                console.log(data)
            }).catch(err=>{
                console.log(err)
            })
        }
    },[image])

    const updatePhoto = (file) => {
        setImage(file)
        
    }

    return (
        <div style={{maxWidth:"600px", margin:'10px auto'}}>
            <div style={{ margin:"20px 20px", borderBottom:"1px solid grey"}}>            
                <div style={{display:"flex", justifyContent:"space-around"}}>
                    <div>
                        <img style={{width:'160px',height:"160px",borderRadius:"80px"}} src="https://images.unsplash.com/photo-1552158732-06dc1d835de0?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60" />
                    </div>
                    <div>
                        <h4>{state?state.name:"loading" }</h4>
                        <div style={{display:"flex", justifyContent:"space-between", width:"110%"}}>
                            <h6>{allpics.length} Post</h6>
                            <h6>{state?state.followers.length:"0"} Followers</h6>
                            <h6>{state?state.following.length:"0"} Following</h6>
                        </div>
                    </div>
                </div>
                <div className="file-field input-field" style={{margin:"30px"}}>
                    <div className="btn">
                        <span>Select Picture</span>
                        <input type="file"  onChange={(e)=>{updatePhoto(e.target.files[0])}} />
                    </div>
                    <div className="file-path-wrapper">
                        <input className="file-path validate" type="text" />
                    </div>
                </div>
            </div>
            
            <div className="gallery" style={{display:"flex", justifyContent:"space-around", flexWrap:"wrap"}}>
                {
                    allpics.map(item=>{
                        return(
                            <img key={item._id} className="galleryItem" src={item.photo} alt={item.title} />
                        )
                    })
                }
            </div>
        </div>
    )
}

export default Myprofile
