import React,{useEffect, useState, useContext} from 'react'
import { UserContext } from '../../App'
import {useParams} from "react-router-dom"

function Userprofile() {
    const [userProfile, setProfile]=useState(null)
    const {state, dispatch} = useContext(UserContext)
    const {userid} = useParams()
    useEffect(() => {
        fetch(`http://localhost:1000/user/${userid}`,{
            headers:{  
                "Authorization":"Hello " +localStorage.getItem("jwt")
            }
        }).then(res=>res.json())
        .then(result=>{
            console.log(result)
            setProfile(result)
        })
    }, [])

    const followUser = () =>{
        fetch("/http://localhost:1000/follow",{
            method:"put",
            headers:{
                "Content-Type":"application/json",
                "Authorization":"Hello "+localStorage.getItem("jwt")
            },
            body:JSON.stringify({
                followId:userid
            })
        }).then(res=>res.json())
        .then(data=>{
            console.log(data)
        })
    }
    return (
        <>
        {userProfile ?
        <div style={{maxWidth:"600px", margin:'10px auto'}}>
            <div style={{display:"flex", justifyContent:"space-around", margin:"20px 20px", borderBottom:"1px solid grey"}}>
                <div>
                    <img style={{width:'160px',height:"160px",borderRadius:"80px"}} src="https://images.unsplash.com/photo-1552158732-06dc1d835de0?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60" />
                </div>
                <div>
                    <h4>{userProfile.user.name }</h4>
                    <div style={{display:"flex", justifyContent:"space-between", width:"110%"}}>
                        <h6>{userProfile.posts.length} Post</h6>
                        <h6>0 Followers</h6>
                        <h6>0 Following</h6>
                    </div>
                    <button className="btn waves-effect waves-light" type="submit" omClick={()=>followUser()}>Follow</button>
                </div>
            </div>

            <div className="gallery" style={{display:"flex", justifyContent:"space-around", flexWrap:"wrap"}}>
                {
                    userProfile.posts.map(item=>{
                        return(
                            <img key={item._id} className="galleryItem" src={item.photo} alt={item.title} />
                        )
                    })
                }
            </div>
        </div>   

        : <h2>loading!...</h2>}
        
        </>
    )
}

export default Userprofile
