import React,{useContext} from 'react'
import {Link} from "react-router-dom"
import { UserContext } from '../../App'

function Navbar() {
    const {state,dispatch}= useContext(UserContext)
    const renderList = () =>{
        if (state){
            return[
                <li><Link to="/myfeed">My Feed</Link></li>,
                <li><Link to="/createpost">Upload Post</Link></li>,
                <li><button className="btn waves-effect waves-light" type="submit" onClick = {()=>{
                    localStorage.clear()
                    dispatch({type:"CLEAR"})
                }} >Logout</button></li>
            ]
        }else{
            return [
                <li><Link to="/login">Log-In</Link></li>,
                <li><Link to="/signup">Sign-Up</Link></li>
            ]
        }
    }
    return (
        <div>
             <nav>
                <div className="nav-wrapper black">
                <Link to={state?"/":"/login"} className="brand-logo left text-white">Picture Gallery</Link>
                <ul id="nav-mobile" className="right ">
                    {renderList()}
                </ul>
                </div>
            </nav>
        </div>
    )
}

export default Navbar
