import React,{useState, useEffect} from 'react'

function Home() {
    const [data,setData] = useState([])
    useEffect(() => {
        fetch('http://localhost:1000/findallpost',{
            headers:{
                "Authorization":"Hello " + localStorage.getItem('jwt')
            }
        }).then(res=>res.json())
        .then(result=>{
            // console.log(result)
            setData(result.posts)
        })
    }, [])

    const likePost = (_id) => {
        fetch("http://localhost:1000/unlike",{
            method:"put",
            headers:{
                "Content-Type":"application/json",
                "Authorization":"Hello " + localStorage.getItem('jwt')
            },
            body:JSON.stringify({
                postId:_id
            })
        }).then(res=>res.json())
        .then(result=>{
            console.log(result)
        })
    }

    const unlikePost = (_id) => {
        fetch("http://localhost:1000/like",{
            method:"put",
            headers:{
                "Content-Type":"application/json",
                "Authorization":"Hello " + localStorage.getItem('jwt')
            },
            body:JSON.stringify({
                postId:_id
            })
        }).then(res=>res.json())
        .then(result=>{
            console.log(result)
        })
    }

    return (
        <div className="home">
            {
                data.map(item=>{
                    return(
                        <div className="card home-card" key={item._id}>
                            <h6>{item.postedbyId.name}</h6>
                            <div className="card image">
                                <img src={item.photo} />
                        
                                <div className="card-content">
                                <i className="material-icons">favorite</i>
                                <i className="material-icons" onClick={()=>{likePost(item._id)}}>thumb_up</i>
                                <i className="material-icons"onClick={()=>{unlikePost(item._id)}}>thumb_down</i>
                                    <h6>{item.likes.length} likes</h6>
                                    <h4>{item.title}</h4>
                                    <p>{item.body}</p>
                                    <input type="text" placeholder="comment something" />
                                </div>
                            </div>
                        </div>

                    )
                })
            }
        </div>
    )
}

export default Home
