import React, { Component } from 'react'
import {Link} from 'react-router-dom'


class SignUp extends Component {
    
    state={
        name:'',
        email:'',
        password:'',
        repassword:''
    }

    submitSignup = ()=>{
        fetch("http://localhost:1000/signup",{
            method:"post",
            headers:{
                "Content-Type":"application/json"
            },
            body:JSON.stringify({
                name:"",
                email:"",
                password:"",
            })
        }).then(res=>res.json())
        .then(data=>{
            console.log(data)
            if(data.err){
              <div className="toast-body">
                  {data.err}
              </div>
            }
        })
    }

    render() {
        return (
            <div className="row signup">
                <div className="col-6 offset-md-3">
                    <div className="form-group">
                            <label htmlFor="name">Name</label>
                            <input type="text" name="name" id="name" className="form-control" placeholder="Enter Your Name" onChange={(e)=>this.setState({name:e.target.value})} />
                            <label htmlFor="email">Email</label>
                            <input type="email" name="email" id="email" className="form-control" placeholder="Enter Your Email" onChange={(e)=>this.setState({email:e.target.value})} />
                            <label htmlFor="password">Password</label>
                            <input type="password" name="password" id="password" className="form-control" placeholder="Enter Your Password" onChange={(e)=>this.setState({password:e.target.value})} />
                            <label htmlFor="repassword">Retype-Password</label>
                            <input type="repassword" name="repassword" id="repassword" className="form-control" placeholder="Enter Your Password" onChange={(e)=>this.setState({repassword:e.target.value})} />
                            <br/>
                            <button className="btn btn-primary" type="submit" onClick={()=>{this.submitSignup()}}>Submit</button>
                            
                    </div>
                    <h6>
                        <Link to="/login">Already have an account?</Link>
                    </h6>
                </div>
            </div>
        )
    }
}

export default SignUp
