import React, {useState} from 'react'
import { Link } from 'react-router-dom'
import M from "materialize-css"


function SignUp() {
    const [name, setName] = useState('')
    const [password, setPassword] = useState('')
    const [repassword, setRePassword] = useState('')
    const [email, setEmail] = useState('')

    const PostData = () =>{
        fetch("http://localhost:1000/signup", {
            method:'post',
            headers:{
                "Content-Type":"application/json"
            },
            body:JSON.stringify({
                name:"",
                password:"",
                email:""
            })
        }).then(res => res.json())
        .then(data =>{
            if(data.error){
                M.toast({html: data.error})
            }
            console.log(data)
        })
    }
    return (
        <div className="login-card">
            <div className="card auth-card">
                <form>
                    <div>
                        <h1 style={{textAlign:"center"}}>Sign-Up</h1>
                        <label htmlFor="name">Full-Name</label>
                        <input type="text" placeholder="Name" value={name} onChange={(e)=>setName(e.target.value)} />
                        <label htmlFor='email'>E-mail:</label>
                        <input type='email' placeholder='Email' value={email} onChange={(e)=>setEmail(e.target.value)} />
                        <label htmlFor='password'>Password:</label>
                        <input type='password' placeholder='Password' value={password} onChange={(e)=>setPassword(e.target.value)} />
                        <label htmlFor='re-password'>ReType-Password:</label>
                        <input type='password' placeholder='Password' value={repassword} onChange={(e)=>setRePassword(e.target.value)} />
                        <button className="btn waves-effect waves-light" type="submit" name="action" onClick={()=>PostData()}>Log-In
                        </button>
                    </div>
                    <div>    
                        <Link to="/login"><h6>Already have a account??</h6></Link>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default SignUp
