import React,{useEffect, useState} from 'react'

function Myprofile() {
    const [allpics, setPics]=useState([])
    useEffect(() => {
        fetch('http://localhost:1000/myuploads',{
            headers:{
                "Authorization":"Hello " +localStorage.getItem('jwt')
            }
        }).then(res=>res.json())
        .then(result=>{
            console.log(result)
            // setPics(result.myuploads)
        })
    }, [])
    return (
        <div style={{maxWidth:"600px", margin:'10px auto'}}>
            <div style={{display:"flex", justifyContent:"space-around", margin:"20px 20px", borderBottom:"1px solid grey"}}>
                <div>
                    <img style={{width:'160px',height:"160px",borderRadius:"80px"}} src="https://images.unsplash.com/photo-1552158732-06dc1d835de0?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60" />
                </div>
                <div>
                    <h4>Siddharth Jaiswal</h4>
                    <div style={{display:"flex", justifyContent:"space-between", width:"110%"}}>
                        <h6>0 Post</h6>
                        <h6>0 Followers</h6>
                        <h6>0 Following</h6>
                    </div>
                </div>
            </div>

            <div className="gallery" style={{display:"flex", justifyContent:"space-around", flexWrap:"wrap"}}>
                {/* {
                    myuploads.map(item=>{
                        return(
                            <img className="galleryItem" src="" />
                        )
                    })
                } */}
            </div>
        </div>
    )
}

export default Myprofile
